// App.jsx - Main component
import React, { useState, useEffect } from 'react';
import './Contacts.css';
import ContactForm from './ContactForm';
import ContactList from './ContactList';

function App() {
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [form, setForm] = useState({ name: '', email: '', phone: '' });
  const [editingId, setEditingId] = useState(null);

  const handleSubmit = (e) => {
    e && e.preventDefault();
    
    if (editingId) {
      // Update existing contact
      const updatedContacts = contacts.map(contact => 
        contact._id === editingId || contact.id === editingId ? { ...form, _id: editingId } : contact
      );
      setContacts(updatedContacts);
      setEditingId(null);
    } else {
      // Add new contact
      setContacts([...contacts, { ...form, _id: Date.now().toString() }]);
    }
    
    // Reset form
    setForm({ name: '', email: '', phone: '' });
  };

  const handleEdit = (contact) => {
    setForm({
      name: contact.name,
      email: contact.email,
      phone: contact.phone
    });
    setEditingId(contact._id || contact.id);
  };

  const handleDelete = (id) => {
    setContacts(contacts.filter(contact => (contact._id || contact.id) !== id));
  };

  // Filter contacts based on search term
  const filteredContacts = contacts.filter(contact => {
    const searchLower = searchTerm.toLowerCase();
    return (
      contact.name.toLowerCase().includes(searchLower) ||
      contact.email.toLowerCase().includes(searchLower) ||
      contact.phone.toLowerCase().includes(searchLower)
    );
  });

  const handleLogout = () => {
    // Handle logout functionality
    console.log('Logging out...');
  };

  return (
    <div className="contacts-container">
      {/* Header */}
      <div className="contacts-header">
        <h1 className="contacts-title">Contacts</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>

      {/* Search Bar */}
      <input
        type="text"
        className="search-input"
        placeholder="Search contacts..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Contact Form */}
      <ContactForm
        handleSubmit={handleSubmit}
        form={form}
        setForm={setForm}
        editingId={editingId}
      />

      {/* Contact List */}
      <ContactList
        contacts={filteredContacts}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />
    </div>
  );
}

export default App;
