import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { createContact, getContacts, updateContact, deleteContact, searchContacts } from './services/api';
import ContactForm from './components/ContactForm';
import ContactList from './components/ContactList';
import Login from './components/Login';
// import { ToastContainer, toast } from 'react-toastify';
// import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', phone: '' });
  const [editingId, setEditingId] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [user, setUser] = useState(null);

  const fetchContacts = async () => {
    const { data } = await getContacts();
    setContacts(data);
  };

  useEffect(() => {
    if (user) {
      fetchContacts();
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await updateContact(editingId, form);
      // toast.success('Contact Updated!');
    } else {
      await createContact(form);
      // toast.success('Contact Added!');
    }
    setForm({ name: '', email: '', phone: '' });
    setEditingId(null);
    fetchContacts();
  };

  const handleEdit = (contact) => {
    setForm(contact);
    setEditingId(contact._id);
  };

  const handleDelete = async (id) => {
    await deleteContact(id);
    // toast.error('Contact Deleted!');
    fetchContacts();
  };

  const handleSearch = async (e) => {
    setSearchQuery(e.target.value);
    if (e.target.value) {
      const { data } = await searchContacts(e.target.value);
      setContacts(data);
    } else {
      fetchContacts();
    }
  };

  const handleLogin = (email) => {
    setUser(email);
  };

  const handleLogout = () => {
    setUser(null);
    setContacts([]);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={user ? <Navigate to="/" /> : <Login onLogin={handleLogin} />}
        />
        <Route
          path="/"
          element={
            user ? (
              <div className="min-h-screen p-4 bg-gray-100">
                {/* <ToastContainer /> */}
                <div className="flex justify-between items-center mb-6 max-w-md mx-auto">
                  <h1 className="text-3xl font-bold">Contact Manager</h1>
                  <button
                    onClick={handleLogout}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Logout
                  </button>
                </div>

                <ContactForm
                  handleSubmit={handleSubmit}
                  form={form}
                  setForm={setForm}
                  editingId={editingId}
                />

                <input
                  type="text"
                  value={searchQuery}
                  onChange={handleSearch}
                  placeholder="Search Contacts..."
                  className="input mt-6 w-full max-w-md mx-auto"
                />

                <ContactList
                  contacts={contacts}
                  handleEdit={handleEdit}
                  handleDelete={handleDelete}
                />
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
