// ContactForm.jsx
import React from 'react';
import './Contacts.css';

function ContactForm({ handleSubmit, form, setForm, editingId }) {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: value,
    }));
  };

  return (
    <div className="contact-form-container">
      <h2 className="form-title">{editingId ? 'Edit Contact' : 'Add New Contact'}</h2>

      <div className="form-group">
        <label htmlFor="name" className="form-label">Name</label>
        <input
          type="text"
          id="name"
          name="name"
          value={form.name}
          onChange={handleChange}
          className="form-input"
          placeholder="John Doe"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="email" className="form-label">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          value={form.email}
          onChange={handleChange}
          className="form-input"
          placeholder="john@example.com"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="phone" className="form-label">Phone</label>
        <input
          type="tel"
          id="phone"
          name="phone"
          value={form.phone}
          onChange={handleChange}
          className="form-input"
          placeholder="+1 (555) 123-4567"
          required
        />
      </div>

      <button onClick={handleSubmit} className="add-contact-button">
        <span className="plus-icon">+</span> {editingId ? 'Update Contact' : 'Add Contact'}
      </button>
    </div>
  );
}

export default ContactForm;
