import React from 'react';

function ContactList({ contacts, handleEdit, handleDelete }) {
  if (!contacts || contacts.length === 0) {
    return <p className="text-center mt-4">No contacts found.</p>;
  }

  return (
    <ul className="max-w-md mx-auto mt-4">
      {contacts.map((contact) => (
        <li
          key={contact._id}
          className="bg-white p-4 my-2 rounded shadow flex justify-between items-center"
        >
          <div>
            <p className="font-semibold">{contact.name}</p>
            <p>{contact.email}</p>
            <p>{contact.phone}</p>
          </div>
          <div>
            <button
              onClick={() => handleEdit(contact)}
              className="mr-2 px-3 py-1 bg-blue-500 text-white rounded"
            >
              Edit
            </button>
            <button
              onClick={() => handleDelete(contact._id)}
              className="px-3 py-1 bg-red-500 text-white rounded"
            >
              Delete
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
}

export default ContactList;
