import React, { useState, useEffect } from 'react';
import { createContact, getContacts, updateContact, deleteContact, searchContacts } from './services/api';
import ContactForm from './components/ContactForm';
import ContactList from './components/ContactList';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', phone: '' });
  const [editingId, setEditingId] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  const fetchContacts = async () => {
    const { data } = await getContacts();
    setContacts(data);
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editingId) {
      await updateContact(editingId, form);
      toast.success('Contact Updated!');
    } else {
      await createContact(form);
      toast.success('Contact Added!');
    }
    setForm({ name: '', email: '', phone: '' });
    setEditingId(null);
    fetchContacts();
  };

  const handleEdit = (contact) => {
    setForm(contact);
    setEditingId(contact._id);
  };

  const handleDelete = async (id) => {
    await deleteContact(id);
    toast.error('Contact Deleted!');
    fetchContacts();
  };

  const handleSearch = async (e) => {
    setSearchQuery(e.target.value);
    if (e.target.value) {
      const { data } = await searchContacts(e.target.value);
      setContacts(data);
    } else {
      fetchContacts();
    }
  };

  return (
    <div className="min-h-screen p-4 bg-gray-100">
      <ToastContainer />
      <h1 className="text-3xl font-bold text-center mb-6">Contact Manager</h1>

      <ContactForm
        handleSubmit={handleSubmit}
        form={form}
        setForm={setForm}
        editingId={editingId}
      />

      <input
        type="text"
        value={searchQuery}
        onChange={handleSearch}
        placeholder="Search Contacts..."
        className="input mt-6 w-full max-w-md mx-auto"
      />

      <ContactList
        contacts={contacts}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />
    </div>
  );
}

export default App;
