const { readFileSync } = require('fs')

module.exports.load = function loadEnv(encoding) {
   const path = `${process.cwd()}/.env`

   readFileSync(path, { encoding })
      .toString()
      .replace(/ +/g, '')
      .split(/\n|\r|\r\n/)
      .forEach(line => {
         const [ key, value ] = line.split('=')
         process.env[key] = value
      })
}